mapper.select:
select

from t_buyer
where
account=#{account,jdbcType=VARCHAR}

    and state=#{state,jdbcType=VARCHAR}

select

from t_buyer
where id = #{id, jdbcType=BIGINT}

select
count(*)
from t_buyer


        contact like "%${contact}%"


        and phone = #{phone, jdbcType=VARCHAR}


        and  create_time > #{timeStart, jdbcType=TIMESTAMP} 


        and  create_time <= #{timeEnd, jdbcType=TIMESTAMP}


select

from t_buyer


        contact like "%${contact}%"


        and phone = #{phone, jdbcType=VARCHAR}


        and  create_time > #{timeStart, jdbcType=TIMESTAMP} 


        and  create_time <= #{timeEnd, jdbcType=TIMESTAMP} 





        order by create_time ${sort.Direction}


        order by update_time desc
